big_test = ''

def part1(inpt):
    for i, (a,b,c,d) in enumerate(zip(inpt, inpt[1:], inpt[2:], inpt[3:])):
        if len({a,b,c,d}) == 4:
            return i + 4
        
def part2(inpt):
    for i, s in enumerate(zip(inpt, inpt[1:], inpt[2:], inpt[3:], inpt[4:], inpt[5:], inpt[6:], inpt[7:], inpt[8:], inpt[9:], inpt[10:], inpt[11:], inpt[12:], inpt[13:])):
        if len(set(s)) == 14:
            return i + 14
    

